const leads = [];
const people = $input.item.json.people || [];

for (const person of people) {
  leads.push({
    firstName: person.first_name || '',
    lastName: person.last_name || '',
    email: person.email || '',
    title: person.title || '',
    company: person.organization?.name || '',
    companyWebsite: person.organization?.website_url || '',
    linkedinUrl: person.linkedin_url || '',
    city: person.city || '',
    state: person.state || '',
    country: person.country || '',
    location: `${person.city || ''}, ${person.state || ''}`.trim(),
    phone: person.phone_numbers?.[0]?.raw_number || '',
    employeeCount: person.organization?.estimated_num_employees || 0
  });
}

return leads.map(lead => ({ json: lead }));

const baseData = $('Parse Apollo Results').item.json;
const clearbitData = $input.item.json;

let enrichedLead = { ...baseData };

if (clearbitData && clearbitData.person) {
  enrichedLead.bio = clearbitData.person.bio || '';
  enrichedLead.avatar = clearbitData.person.avatar || '';
  enrichedLead.twitter = clearbitData.person.twitter?.handle || '';
  enrichedLead.linkedin = clearbitData.person.linkedin?.handle || enrichedLead.linkedinUrl;
}

if (clearbitData && clearbitData.company) {
  enrichedLead.companyDescription = clearbitData.company.description || '';
  enrichedLead.companyIndustry = clearbitData.company.industry || '';
  enrichedLead.companyTech = clearbitData.company.tech || [];
}

return { json: enrichedLead };

const item = $input.item.json;
const website = item.companyWebsite;

if (!website || !website.startsWith('http')) {
  return {
    json: {
      ...item,
      companyDescription: item.companyDescription || 'No website available',
      services: [],
      websiteScraped: false
    }
  };
}

try {
  const response = await this.helpers.httpRequest({
    method: 'GET',
    url: website,
    timeout: 10000,
    headers: {
      'User-Agent': 'Mozilla/5.0 (compatible; LeadBot/1.0)'
    }
  });

  let text = response.replace(/<script[^>]*>.*?<\/script>/gi, '')
    .replace(/<style[^>]*>.*?<\/style>/gi, '')
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  const serviceKeywords = ['service', 'solution', 'offer', 'product', 'platform'];
  const services = [];

  serviceKeywords.forEach(keyword => {
    const regex = new RegExp(`([^.]*${keyword}[^.]*\.)`, 'gi');
    const matches = text.match(regex);
    if (matches) {
      services.push(...matches.slice(0, 2));
    }
  });

  return {
    json: {
      ...item,
      companyDescription: item.companyDescription || text.substring(0, 500),
      services: services.slice(0, 5),
      websiteScraped: true
    }
  };
} catch (error) {
  return {
    json: {
      ...item,
      companyDescription: item.companyDescription || 'Unable to scrape website',
      services: [],
      websiteScraped: false
    }
  };
}

