const baseData = $('Filter High Score Leads').item.json;
const emailResponse = $('Generate Email Sequence').item.json;
const linkedinResponse = $('Generate LinkedIn Sequence').item.json;

let emailSequence = {
  email1: { subject: '', body: '', cta: '' },
  email2: { subject: '', body: '', cta: '' },
  email3: { subject: '', body: '', cta: '' }
};

let linkedinSequence = {
  message1: '',
  message2: '',
  message3: ''
};

try {
  const emailText = emailResponse.content[0].text;
  const cleanEmail = emailText
    .replace(/```json\n?/g, '')
    .replace(/```\n?/g, '')
    .trim();
  emailSequence = JSON.parse(cleanEmail);
} catch (error) {
  console.error('Error parsing email sequence:', error);
}

try {
  const linkedinText = linkedinResponse.content[0].text;
  const cleanLinkedIn = linkedinText
    .replace(/```json\n?/g, '')
    .replace(/```\n?/g, '')
    .trim();
  linkedinSequence = JSON.parse(cleanLinkedIn);
} catch (error) {
  console.error('Error parsing LinkedIn sequence:', error);
}

const replaceNames = (text) => {
  return text
    .replace(/\[First Name\]/g, baseData.firstName)
    .replace(/\[Company\]/g, baseData.company);
};

return {
  json: {
    ...baseData,
    emailSequence: {
      email1: {
