// Calculate lead score based on questionnaire
const creditScore = $input.item.json.creditScore || 0;
const income = $input.item.json.annualIncome || 0;
const loanAmount = $input.item.json.requestedLoanAmount || 0;

let score = 0;

// Credit score scoring (max 40 points)
if (creditScore >= 750) score += 40;
else if (creditScore >= 700) score += 30;
else if (creditScore >= 650) score += 20;
else if (creditScore >= 600) score += 10;

// Income scoring (max 30 points)
if (income >= 100000) score += 30;
else if (income >= 75000) score += 20;
else if (income >= 50000) score += 15;
else if (income >= 30000) score += 10;

// Loan-to-income ratio (max 30 points)
const loanToIncome = income > 0 ? loanAmount / income : 0;
if (loanToIncome <= 2) score += 30;
else if (loanToIncome <= 3) score += 20;
else if (loanToIncome <= 4) score += 10;

return {
  json: {
    ...($input.item.json),
    leadScore: score,
    scoreCalculatedAt: new Date().toISOString()
  }
};

